mapper.select:
SELECT id,coupon_name,coupon_type,limit_condition,apply_range, DATE_FORMAT(deadline,'%Y-%m-%d')as deadline, coupon_desc,min_price,discount,discount_desc, ( CASE WHEN deadline > now() THEN '未过期' ELSE '已过期' END ) AS deadline_state,create_time FROM coupon WHERE
`status` <> 0 AND kind=0
AND coupon_name like concat('%',#{couponName},'%')
AND discount_desc like concat('%',#{couponDesc},'%')
AND coupon_type= #{couponType}
AND deadline >= #{deadlineStartTime}
AND deadline <= #{deadlineStopTime}
AND create_time >= #{createStartTime}
AND create_time <= #{createStopTime}
AND deadline < now()
AND deadline >= now()
ORDER BY create_time DESC

SELECT id, coupon_name, coupon_type, limit_condition,apply_range, DATE_FORMAT(deadline,'%Y-%m-%d')as deadline, DATE_FORMAT(display_start_time,'%Y-%m-%d')as display_start_time , DATE_FORMAT(display_stop_time,'%Y-%m-%d')as display_stop_time, `status`,coupon_desc,min_price,discount,discount_desc, ( CASE WHEN `status`=1 THEN ( CASE WHEN now() < display_start_time AND now() < display_stop_time THEN '预约展示' WHEN now() > display_start_time AND now() > display_stop_time THEN '已展示' ELSE '展示中' END ) END ) AS coupon_state, create_time FROM coupon WHERE shop_id=#{shopId} AND `status` <> 0
AND `status`= #{status}
AND coupon_name like concat('%',#{couponName},'%')
AND discount_desc like concat('%',#{couponDesc},'%')
AND deadline >= #{deadlineStartTime}
AND deadline <= #{deadlineStopTime}
AND create_time >= #{createStartTime}
AND create_time <= #{createStopTime}
AND now() < display_start_time AND now() < display_stop_time
AND now() >= display_start_time AND now() <= display_stop_time
AND now() > display_start_time AND now() > display_stop_time
ORDER BY create_time DESC
